configuration.default_value:
{
    zone-1 = [
      {
        name           = "subnet-a"
        cidr           = "10.10.10.0/24"
        public_gateway = true
        acl_name       = "vpc-acl"
        no_addr_prefix = false
      }
    ],
    zone-2 = [
      {
        name           = "subnet-b"
        cidr           = "10.20.10.0/24"
        public_gateway = true
        acl_name       = "vpc-acl"
        no_addr_prefix = false
      }
    ],
    zone-3 = []
  }

[
  {
    name                         = "vpc-acl"
    add_ibm_cloud_internal_rules = true
    add_vpc_connectivity_rules   = true
    prepend_ibm_rules            = true
    rules = [
      {
        name      = "allow-all-443-inbound"
        action    = "allow"
        direction = "inbound"
        tcp = {
          port_min = 443
          port_max = 443
        }
        destination = "0.0.0.0/0"
        source      = "0.0.0.0/0"
      },
      {
        name      = "allow-all-80-inbound"
        action    = "allow"
        direction = "inbound"
        tcp = {
          port_min        = 80
          port_max        = 80
          source_port_min = 80
          source_port_max = 80
        }
        destination = "0.0.0.0/0"
        source      = "0.0.0.0/0"
      },
      {
        name      = "allow-all-ingress-inbound"
        action    = "allow"
        direction = "inbound"
        tcp = {
          source_port_min = 30000
          source_port_max = 32767
        }
        destination = "0.0.0.0/0"
        source      = "0.0.0.0/0"
      },
      {
        name      = "allow-all-443-outbound"
        action    = "allow"
        direction = "outbound"
        tcp = {
          source_port_min = 443
          source_port_max = 443
        }
        destination = "0.0.0.0/0"
        source      = "0.0.0.0/0"
      },
      {
        name      = "allow-all-80-outbound"
        action    = "allow"
        direction = "outbound"
        tcp = {
          source_port_min = 80
          source_port_max = 80
          port_min        = 80
          port_max        = 80
        }
        destination = "0.0.0.0/0"
        source      = "0.0.0.0/0"
      },
      {
        name      = "allow-all-ingress-outbound"
        action    = "allow"
        direction = "outbound"
        tcp = {
          port_min = 30000
          port_max = 32767
        }
        destination = "0.0.0.0/0"
        source      = "0.0.0.0/0"
      }
    ]
  }
]
